class Solution {
    fun countOfPairs(nums: IntArray): Int {
        val n = nums.size
        val N = 50
        val MOD = 1_000_000_007

        val dp = MutableList(n) { MutableList(N + 1) { 0 } }
        for (i in 0..nums[0]) {
            dp[0][i] = 1
        }

        for (pos in 1 until n) {
            for (last in 0..nums[pos]) {
                for (x in 0..last) {
                    val y = nums[pos - 1] - x
                    val otherLast = nums[pos] - last
                    if (y < otherLast) continue
                    dp[pos][last] += dp[pos - 1][x]
                    dp[pos][last] %= MOD
                }
            }
        }

        var ans = 0
        for (i in 0..N) {
            ans += dp.last()[i]
            ans %= MOD
        }
        return ans
    }
}
