class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp, 0, sizeof dp);
        memset(dp2, 0, sizeof dp2);

        const int N = nums.size();
        for (int a = 0;a <= nums[N - 1]; a++) {
            dp[a] = 1;
        }

        for (int i = N - 2;i >= 0;i--) {
            memset(dp2, 0, sizeof dp2);
            for (int a = 0;a <= nums[i]; a++) {
                int b = nums[i] - a;
                // a, b
                for (int c = 0;c <= a && c <= nums[i + 1]; c++) {
                    int d = nums[i + 1] - c;
                    if (d >= b) {
                        dp2[a] += dp[c];
                        dp2[a] %= MOD;
                    }
                }
            }
            memcpy(dp, dp2, sizeof dp2);
        }

        long long ret = 0;
        for (int a = 0;a <= nums[0]; a++) ret = (ret + dp[a]) % MOD;
        return (int) ret;
    }
private:
    static const long long MOD = 1000000007LL;
    static const int MAXN = 2048, MAXV = 64;
    long long dp[MAXV], dp2[MAXV];
};
