class Solution {
    int mod = 1e9 + 7;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> v(n);
        v[n - 1] = vector<int>(nums[n - 1] + 1, 1);
        for(int i = n - 2; i >= 0; i--)
        {
            v[i] = vector<int>(nums[i] + 1, 0);
            int diff = max(0, nums[i + 1] - nums[i]);
            for(int j = 0; j <= nums[i]; j++)
            {
                for(int k = j + diff; k <= nums[i + 1]; k++)
                {
                    v[i][j] = ((long) v[i][j] + v[i + 1][k]) % mod;
                }
            }
        }
        int sum = 0;
        for(int val : v[0])
            sum = (sum + val) % mod;
        return sum;
    }
};
