class Solution {
public:
    const int mod = 1e9+7;
    const int mxn = 2020;
    // const int mxn = 6;
    int mad(int a,int b){
        a += b;
        return a>=mod?a-mod:a;
    }
    int countOfPairs(vector<int>& nums) {
        vector<int> dp(mxn,0);
        for(int i = 0;i<=nums[0];i++)dp[i] = 1;
        for(int i = 1;i<dp.size();i++)dp[i] = mad(dp[i-1],dp[i]);
        for(int i = 1;i<nums.size();i++){
            vector<int> pre(mxn,0);
            pre.swap(dp);
            int now = nums[i];
            for(int j = 0;j<=now;j++){
                int able = min(j,nums[i-1]-(now-j));
                if(able>=0)dp[j] = pre[able];
            }
            for(int j = 1;j<dp.size();j++)dp[j] = mad(dp[j-1],dp[j]);
        }
        int ans = 0;
        return dp.back();
    }
};
