class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<long long> dp(1005);
        for(long long i = 0; i <= a[0]; i++) dp[a[0] - i] = 1;
        const long long M = 1E9 + 7;
        for(long long i = 1; i < n; i++){
            vector<long long> ndp(a[i] + 1);
            queue<long long> q;
            long long cur = 0;
            for(long long x = 0; x <= a[i]; x++){
                q.push(a[i - 1] - x);
                while(!q.empty() && q.front() >= a[i] - x){
                    if(q.front() <= a[i - 1]) cur = (cur + dp[q.front()]) % M;
                    q.pop();
                }
                ndp[a[i] - x] = cur;
            }
            dp = ndp;
        }
        long long ans = 0;
        for(auto i : dp) ans = (ans + i) % M;
        return ans;
    }
};
