class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int ans =0;
        int n = nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(51,vector<int>(51,0)));
        int mod = 1e9+7;
        for(int i=0;i<n;i++)
        {
            if(i==0)
            {
                for(int j=0;j<=nums[i];j++)
                {
                    dp[i][j][nums[i]-j]=1;

                }
            }
            else
            {
               for(int j=0;j<=nums[i];j++)
                {
                    int k = nums[i]-j;

                    for(int l=0;l<=nums[i-1];l++)
                    {
                        if(j>=l&&k<=(nums[i-1]-l))
                        {
                            dp[i][j][k]+=dp[i-1][l][nums[i-1]-l];
                            dp[i][j][k]%=mod;
                        }
                    }
                }
            }
        }
        for(int j=0;j<=nums[n-1];j++)
        {
            ans+=dp[n-1][j][nums[n-1]-j];
           // cout<<ans<<endl;
            ans%=mod;
        }
        return ans;
    }
};
