class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int m = 1e9 + 7;
        vector<vector<int> >table (2, vector<int>(51, 0));
        for(int i = 0; i <= nums[0]; i++) table[0][i] = 1;
        int x, y, x2, y2;
        int c = 1;
        for(int i = 1; i < nums.size(); i++){
            table[c] = vector<int>(51, 0);
            x = nums[i - 1];
            y = nums[i];
            for(int j = 0; j <= y; j++){
                y2 = y - j;
                for(int k = 0; k <= j && k <= x; k++){
                    x2 = x - k;
                    if(x2 < y2) continue;
                    table[c][j] = (table[c][j] + table[c^1][k])%m;
                }
            }
            c ^= 1;
        }
        c ^= 1;
        int ans = 0;
        for(int i = 0; i < 51; i++){
            ans = (ans + table[c][i])%m;
        }
        return ans;
    }
};
