class Solution {
    int dp[][][];
    int mod = (int)1e9+7;
    public int countOfPairs(int[] nums) {
        long ans = 0;
        this.dp = new int[nums.length][51][51];
        for(int a[][] : dp)
            for(int b[] : a)
                Arrays.fill(b,-1);
        for(int nf = 0 ; nf <= nums[0] ; nf++){
            int ns = nums[0]-nf;
            ans += rec(nums,1,nf,ns);
            ans %= mod;
        }
        return (int)ans;
    }
    private int rec(int[] nums , int i , int f , int s){
        if(i == nums.length)return 1;
        long ans = 0;
        if(dp[i][f][s] != -1)return dp[i][f][s];
        for(int nf = 0 ; nf <= nums[i] ; nf++){
            int ns = nums[i]-nf;
            if(nf>=f&&ns<=s){
                ans += rec(nums , i+1 , nf , ns);
                ans %= mod;
            }
        }
        return dp[i][f][s] = (int)ans;
    }
}
