class Solution {
public:
    typedef long long ll;
    int MOD = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<ll>> dp(n, vector<ll>(51, 0));

        for (int x = 0; x <= nums[0]; x++) {
            dp[0][x] = 1;
        }

        for (int i = 1; i < n; i++) {
            int z = nums[i];
            int pz = nums[i-1];

            for (int x = 0; x <= z; x++) {
                int y = z - x;

                for (int px = 0; px <= pz; px++) {
                    int py = pz - px;

                    if (x >= px && y <= py) {
                        dp[i][x] += dp[i-1][px];
                        dp[i][x] %= MOD;
                    }
                }
            }
        }

//         cout << endl;
//         for (int i = 0; i < n; i++) {
//             for (int j = 0; j <= nums[i]; j++) {
//                 cout << dp[i][j] << " ";
//             }
//             cout << endl;
//         }

        ll res = 0;
        for (int x = 0; x <= nums[n-1]; x++) {
            res += dp[n-1][x];
            res %= MOD;
        }

        return res;
    }
};
