class Solution {
    static final long MOD = (long) 1e9 + 7;

    public int countOfPairs(int[] nums) {
        int n = nums.length;

        long[][] dp = new long[n][51];
        for (int x = 0; x <= nums[0]; ++x) dp[0][x] = 1;

        for (int i = 1; i < n; ++i) {
            for (int cur = 0; cur <= nums[i]; ++cur) {
                for (int prev = 0; prev <= cur; ++prev) {
                    int cur2 = nums[i] - cur;
                    int prev2 = nums[i - 1] - prev;
                    if (prev2 < cur2) continue;
                    dp[i][cur] = (dp[i][cur] + dp[i - 1][prev]) % MOD;
                }
            }
        }

        long res = 0;
        for (long cnt : dp[n - 1]) res = (res + cnt) % MOD;
        return (int) res;
    }
}
