class Solution {
public:
    using ll = long long;
    const ll mod = 1e9 + 7;
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<ll> > dp(n + 2, vector<ll> (52, 0));
        for(int i = 0; i <= a[0]; i++) {
            dp[0][i] = 1;
        }
        for(int i = 1; i < n; i++) {
            for(int pv = 0; pv <= 50; pv++) {
                for(int j = pv; j <= a[i]; j++) {
                    if(a[i - 1] - pv >= a[i] - j) dp[i][j] = (dp[i][j] + dp[i - 1][pv]) % mod;
                }
            }
        }
        ll res = 0;
        for(int i = 0; i <= 50; i++) {
            res = (res + dp[n - 1][i]) % mod;
        }
        return res;
    }
};
