class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        modulo = 10**9 + 7


        @lru_cache(None)
        def recur_fn(i,prev_top,prev_bottom):
            if i == len(nums):
                return 1
            else:
                if nums[i] < prev_top:
                    return 0
                res = 0
                for x in range(prev_top,nums[i]+1):
                    if x >= prev_top and nums[i]-x >= 0 and nums[i]-x <= prev_bottom:
                        res += recur_fn(i+1,x,nums[i]-x)
                        if res >= modulo:
                            res %= modulo
                return res
        return recur_fn(0,0,50)%modulo
