class Solution {
    const int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int dd(vector<int>& a, int i, int s, int e, int n) {
        if (i == n)
            return 1;
        if (dp[i][s][e] != -1)
            return dp[i][s][e];
        long long ans = 0;
        for (int j = s; j <= 50; j++) {
            int k = a[i] - j;
            if (k <= e&&k>=0)
                ans = (ans + dd(a, i + 1, j, k, n)) % mod;
        }
        return dp[i][s][e] = ans;
    }

public:
    int countOfPairs(vector<int>& a) {
        int n = a.size(), ans;
        dp = vector<vector<vector<int>>>(
            n, vector<vector<int>>(52, vector<int>(52, -1)));
        ans = dd(a, 0, 0, 50, n);
        return ans;
    }
};
