class Solution:
    def countOfPairs(self, nums):
        mod = 10**9 + 7
        n = len(nums)
        f = [[0] * 1005 for _ in range(n)]
        g = [[0] * 1005 for _ in range(n)]

        for i in range(nums[0] + 1):
            f[0][i] = 1

        g[0][0] = f[0][0]

        for j in range(1, 1001):
            g[0][j] = (g[0][j - 1] + f[0][j]) % mod

        for i in range(1, n):
            for j in range(nums[i] + 1):
                mx = min(min(nums[i - 1], j), nums[i - 1] + j - nums[i])
                if mx >= 0:
                    f[i][j] = g[i - 1][mx]
                else:
                    f[i][j] = 0

            g[i][0] = f[i][0]
            for j in range(1, 1001):
                g[i][j] = (g[i][j - 1] + f[i][j]) % mod

        return g[n - 1][1000]
