class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        int n = nums.size();

        long long mod = 1000000007;

        vector<vector<long long>> memo(n, vector<long long>(51, 0));

        for (int i = 0; i <= nums[0]; ++i) {
            memo[0][i] = 1;
        }

        for (int i = 0; i < n - 1; ++i) {
            for (int j = 0; j < 51; ++j) {
                if (memo[i][j] > 0) {
                    for (int jj = j; jj <= nums[i+1]; ++jj) {
                        if (nums[i+1] - jj <= nums[i] - j) {
                            memo[i+1][jj] += memo[i][j];
                            memo[i+1][jj] %= mod;
                        }
                    }
                }
            }
        }

        long long sol = 0;

        for (int i = 0; i < 51; ++i) {
            sol += memo[n-1][i];
            sol %= mod;
        }
        return sol;
    }
};
