class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        int m = *max_element(a.begin(), a.end()) + 1;
        vector <vector <long>> dp(n, vector <long>(m, 0));
        int mod = 1e9 + 7;
        for (int j = 0; j <= a.back(); j++)
            dp[n - 1][j] = 1;
        for (int i = n - 2; i >= 0; i--) { 
            long cur = 0;
            int k = a[i] < a[i + 1] ? a[i + 1] : a[i];
            for (int j = a[i]; j >= 0; j--, k--) {
                cur = (cur + dp[i + 1][k]) % mod;
                dp[i][j] = cur;
            }
        }
        int ret = 0;
        for (int j = 0; j <= a[0]; j++) 
            ret = (ret + dp[0][j]) % mod;
        return ret;
    }
};
