class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        dp = [[0] * (max(nums)+1) for _ in range(n)]

        for x in range(nums[0]+1):
            dp[0][x] = 1

        for i in range(1, n):
            for x in range(nums[i]+1):
                total = 0
                for y in range(x+1):
                    if (nums[i-1] - y) >= (nums[i] - x):
                        total = (total + dp[i-1][y]) % mod
                dp[i][x] = total

        res = 0
        for val in dp[n-1]:
            res = (res + val) % mod

        return res
