class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long prev[]=new long[51];
        Arrays.fill(prev,1);
        int mod = (int)1e9+7;
        for(int i = 1;i<n;i++){
            long dp[]=new long[51];
            for(int j = 0;j<=nums[i];j++){
                for(int k=0;k<=j;k++){
                    int b1=nums[i]-j;
                    int b2=nums[i-1]-k;
                    if(b1<=b2) dp[j]=(dp[j]+prev[k])%mod;
                }
            }
            prev = dp;
        }
        long ans = 0;
        for(int i=0;i<=nums[n-1];i++) ans = (ans+prev[i])%mod;
        return (int)ans;

    }
}
