const int M = 1e9 + 7;

class Solution {
    void madd(int& a, int b) {
        a += b;
        if (a >= M) a -= M;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        auto f = [&](auto& self, int p, int a, int b) -> int {
            if (p == n) return 1;
            if (dp[p][a][b] != -1) return dp[p][a][b];

            int res = 0;
            for (int i = a; i <= nums[p]; i++)
                if (nums[p] - i <= b)
                    madd(res, self(self, p + 1, i, nums[p] - i));
            return dp[p][a][b] = res;
            };

        return f(f, 0, 0, 50);
    }
};
