class Solution {
    const int mm = 1e9 + 7;

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> f(2, vector<int>(1001));
        vector<vector<int>> sum(2, vector<int>(1001));
        int h = 0;
        for (int i = 0; i <= nums[0]; i++) {
            f[0][i] = 1;
            sum[0][i] = i + 1;
        }
        for (int i = 1; i < n; i++) {
            h = 1 - h;
            for (int j = 0; j <= nums[i]; j++) {
                int k = min(j, j + nums[i - 1] - nums[i]);
                f[h][j] = k >= 0? sum[1 - h][k]: 0;
                sum[h][j] = ((j? sum[h][j - 1]: 0) + f[h][j]) % mm;
            }
        }
        return sum[h][nums[n - 1]];
    }
};
