class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9+7;
        vector<vector<int>> dp(n,vector<int>(52,-1));
        function<int(int,int,int)> rec = [&](int i,int a,int b){
            if(i<0){
                return 1;
            }
            if(dp[i][a]!=-1)return dp[i][a];
            long long ans=0;
            for(int j=min(a,nums[i]);j>=0;j--){
                if(nums[i]-j>=b){
                    ans+=rec(i-1,j,nums[i]-j);
                    ans%=mod;
                }
            }
            return dp[i][a]=(int)(ans);
        };
        return rec(n-1,51,-1);
    }
};
