class Solution {
  public int countOfPairs(int[] nums) {
    var last = nums[nums.length - 1];
    var dp = new int[last + 1][nums.length];
    for (var i = 0; i <= last; i++) {
      dp[i][nums.length - 1] = i + 1;
    }
    for (var c = nums.length - 2; c >= 0; c--) {
      for (var r = 0; r <= last; r++) {
        var b = Math.max(nums[c + 1] - nums[c] + (last - r), last - r);
        if (b > last) {
          dp[r][c] = (r == 0 ? 0 : dp[r - 1][c]);
        } else {
          dp[r][c] = (dp[last - b][c + 1] + (r == 0 ? 0 : dp[r - 1][c])) % 1000000007;
        }
      }
    }
    return dp[last][0];
  }
}
