class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        max_num = 50
        min_num = 0
        n = len(nums)

        @cache
        def dp(i, prev1_lb, prev2_up):
            if i == n: return 1
            res = 0
            for x in range(prev1_lb, max_num+1):
                if x > nums[i]: break
                y = nums[i] - x
                if 0 <= y <= prev2_up:
                    res = (res + dp(i+1, x, y)) % MOD
                # for y in range(min_num, prev2_up+1):
                #     if x+y > nums[i]: break
                #     if x+y == nums[i]:
                #         res = (res + dp(i+1, x, y)) % MOD
            return res % MOD

        return dp(0, min_num, max_num)
