const int M=(int)1e9+7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<long long> dp1(51);
        int n=nums.size();
        for(int i=0;i<=nums[0];++i){
            dp1[i]=1;
        }
        for(int i=1;i<n;++i){
            vector<long long> dp2(51);
            for(int j=0;j<=50;++j){
                int other=nums[i-1]-j;
                if(other<0)break;
                for(int k=0;k<=50;++k){
                    int rem=nums[i]-k;
                    if(rem<0)break;
                    if(k>=j&&rem<=other){
                        dp2[k]+=dp1[j];
                        if(dp2[k]>=M)dp2[k]-=M;
                    }
                }
            }
            dp1=dp2;
        }
        int res=0;
        for(int i=0;i<=50;++i){
            res+=dp1[i];    if(res>=M)res-=M;
        }
        return res;
    }
};
