class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        mx = max(nums)
        dp = [[0] * (mx + 1) for _ in range(n)]


        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            for j in range(0, nums[i] + 1):
                # arr1: j2 <= j
                # arr1: j2 <= nums[i - 1]
                # arr2: nums[i - 1] - j2 >= nums[i] - j ==> j2 <= nums[i - 1] - nums[i] + j
                for j2 in range(0, min(j, nums[i - 1], nums[i - 1] - nums[i] + j) + 1):
                    dp[i][j] += dp[i - 1][j2]
                    dp[i][j] %= MOD

        return sum(dp[n - 1]) % MOD
