class Solution {
    public int countOfPairs(int[] nums) {
        return sln1(nums);
    }

    static int mod = 1_000_000_007;

    private int sln1(int[] nums){
        int n = nums.length;
        int max = 51;
        int[][] dp = new int[n][max];   //{i, j} number of monotonic array end at i with value j, 
        for(int i = 0;i<=nums[0]; i++) dp[0][i] = 1;
        for(int i = 1;i<n;i++){
            for(int j = 0;j<=nums[i];j++){
                for(int k = 0;k<=nums[i-1];k++){
                    if(k<=j && nums[i-1]-k >= nums[i]-j ) dp[i][j] = (dp[i][j]+dp[i-1][k])%mod;
                }
            }
        }
        int res = 0;
        for(int i: dp[n-1]) res= (res+i)%mod;
        return res;
    }
}
