class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7

        @cache
        def count(i, prev):
            if i == n:
                return 1

            prev2 = nums[i-1] - prev if i > 0 else inf
            ret = 0
            for val in range(prev, nums[i]+1):
                val2 = nums[i] - val
                if val2 <= prev2:
                    ret = (ret + count(i+1, val)) % MOD

            return ret

        return count(0, 0)
