class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9 + 7;
        vector<vector<int>> dp(n, vector<int>(51, 0));

        // init dp[n-1]
        for (int x = 0; x <= nums[n - 1]; x++) {
            dp[n - 1][x] = 1;
        }

        for (int i = n - 2; i >= 0; i--) {
            // set arr1[i] = x
            for (int x = 0; x <= nums[i]; x++) {
                // arr1[i + 1]
                int y = nums[i + 1] - nums[i] + x;
                y = max(x, y);
                while (y <= nums[i + 1]) {
                    dp[i][x] = (dp[i][x] + dp[i + 1][y]) % mod;
                    y++;
                }
            }
        }

        int ans = 0;
        for (int x = 0; x <= nums[0]; x++) {
            ans = (ans + dp[0][x]) % mod;
        }

        return ans;
    }
};
