class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod  = 10**9 + 7
        N  = len(nums)
        @cache
        def go(i,last1,last2):
            if i==N:
                return 1
            ans = 0
            for j in range(nums[i]+1):
                if last1<=j and last2>=nums[i]-j:
                    ans+=go(i+1,j,nums[i]-j)
            return ans%mod
        return (go(0,-float('inf'),float('inf')))%mod
