class Solution {
public:
    int countOfPairs(vector<int>& nums) {
      long long mod=1e9+7;
      int n=nums.size();
      int dp[n][52][52];
      for (int i=0; i<n; i++)
      for (int j=0; j<=50; j++)
      for (int z=0; z<=50; z++)
      dp[i][j][z]=0;
      for (int j=0; j<=nums[0]; j++){
          dp[0][j][nums[0]-j]=1;
        }
        long long tmp;
      for (int i=1; i<n; i++){
        for (int j=0; j<=nums[i]; j++){
           for (int jj=0; jj<=nums[i-1]; jj++){
            if (jj<=j && nums[i-1]-jj>=nums[i]-j)
            dp[i][j][nums[i]-j]=(dp[i][j][nums[i]-j]+dp[i-1][jj][nums[i-1]-jj])%mod;
           }
        }
      }
      int ans=0;
      for (int i=0; i<=nums[n-1]; i++){
         ans=(ans+dp[n-1][i][nums[n-1]-i])%mod;
      }
      return ans;
    }
};
