CONST = pow(10, 9) + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        if not nums:
            return 0

        max_val = max(nums)
        dp = [ [0] * (max_val + 1) for _ in range(len(nums))]

        for i in range(nums[0] + 1):
            dp[0][i] = 1

        for i in range(1, len(nums)):
            for v in range(nums[i] + 1):
                # dp[i][v]

                # v, nums[i] - v
                # prev_1 <= v, prev_2 >= nums[i] - v
                # prev_1 <= v, nums[i - 1] - prev1 >= nums[i] - v
                # prev_1 <= v, prev1 <= nums[i - 1] - nums[i] + v

                max_prev_v = min(v, nums[i - 1] - nums[i] + v)
                if max_prev_v < 0:
                    continue

                for prev_v in range(max_prev_v + 1):
                    dp[i][v] += dp[i - 1][prev_v]
                    dp[i][v] %= CONST

        res = 0
        for v in dp[-1]:
            res += v
            res %= CONST
        return res
