class Solution {
public:

    int mod = 1e9+7;

    int solve(int ind, vector<int>& v, int c1, int c2, int n, vector<vector<int>>& dp){
        if(ind==n) return 1;
        if(dp[ind][c1]>-1) return dp[ind][c1];
        int ans = 0;
        for(int i = 0;i<=c2;i++){
            if((v[ind]-i) >= c1) ans =  (ans + solve(ind+1, v, v[ind]-i, i, n, dp))%mod;
        }
        return dp[ind][c1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        long long ans = 0;
        vector<vector<int>> dp(n, vector<int>(52, -1));
        return solve(0, nums, 0, 51, n, dp);
    }
};
