#include <bits/stdc++.h>
using namespace std; 

#define ll long long

const int MOD = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& A) {
        int n = A.size();
        vector<vector<ll>> dp(n, vector<ll>(1005, 0));

        for (int i = 0; i < n; i++){
            for (int j = 0; j <= A[i]; j++){
                if (i == 0){
                    dp[i][j] = 1;
                }
                else{
                    int lim = min(j, A[i - 1] + j - A[i]);

                    if (lim >= 0){
                        dp[i][j] = dp[i - 1][lim];
                        // cout << i << " " << j <<" --> " << lim << endl;
                    }

                    // cout << i << " " << j <<": " << dp[i][j] << endl;
                }
            }
            for (int j = 1; j <= 1000; j++)
                dp[i][j] = (dp[i][j] + dp[i][j - 1]) % MOD;
        }
        return dp[n - 1][1000];
    }
};
