#define MOD 1000000007

class Solution {
private:
    int A[2000][51];
public:
    int countOfPairs(vector<int>& nums) {
        int n = (int)nums.size();
        for(int i=0;i<n;i++){
            fill(A[i],A[i]+51,0);
        }

        for(int i=0;i<=nums[0];i++){
            A[0][i] = 1;
        }

        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                int j1 = nums[i] - j;
                for(int k=0;k<=min(j,nums[i-1]);k++){
                    int k1 = nums[i-1] - k;
                    if(k1 >= j1){
                        A[i][j] += A[i-1][k];    
                        A[i][j] %= MOD;
                    }
                }
            }
        }

        int ret = 0;
        for(int i=0;i<=nums[n-1];i++){
            ret += A[n-1][i];
            ret %= MOD;
        }

        return ret;
    }
};
