class Solution {
public:
	int dp[2005][52] ;
	int go( int i , int pre , int pre2 , vector< int > &nums  )
	{
		if ( i == si(nums) )return 1 ;

		int &ans = dp[i][pre];

		if ( ans != -1 )return ans ;

		int c = 0 ;

		for ( int val = pre ; val <= nums[i] ; val++ )
		{
			int rem = nums[i] - val ;

			if ( rem <= pre2 )
				c = ( 0LL + c + go( i + 1 , val , rem , nums ) % mod ) % mod ;
		}
		return ans = c ;

	}
	int countOfPairs(vector<int>& nums) {
		memset(dp, -1, sizeof(dp));

		int res = go( 0 , 0 , 52 , nums  ) % mod ;

		return res ;
	}
};
