class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp = vector(nums.size(), vector(51, 0));
        for(int i = 0; i <= nums[0]; i++) dp[0][i] = 1;
        int MOD = 1e9+7;

        for(int i = 1; i < nums.size(); i++){
            for(int cur = 0; cur <= nums[i]; cur++){
                for(int prev = 0; prev <= nums[i-1]; prev++){
                    if((cur >= nums[i] - nums[i-1] + prev) && (cur >= prev)){
                        dp[i][cur] += dp[i-1][prev];
                        dp[i][cur] %= MOD;
                    }
                }
            }
        }

        int ans = 0;
        for(auto num:dp[nums.size()-1]){
            ans += num;
            ans %= MOD;
        }
        return ans;
    }
};
