class Solution {
public:
    int countOfPairs(vector<int>& abc) {
        int n = abc.size();
        vector<vector<int>> S(n, vector<int>(101, 0));
        const int MOD = 1e9 + 7;
        for (int aa = 0; aa <= abc[0]; ++aa) {
            int bb = abc[0] - aa;
            S[0][aa] = 1;
        }
        for (int i = 1; i < n; ++i) {
            for (int cc = 0; cc <= abc[i]; ++cc) {
                int dd = abc[i] - cc;
                for (int ee = 0; ee <= cc; ++ee) {
                    int ff = abc[i-1] - ee;
                    if (ff >= dd) {
                        S[i][cc] = (S[i][cc] + S[i-1][ee]) % MOD;
                    }
                }
            }
        }
        int gg = 0;
        for (int hh = 0; hh <= abc[n-1]; ++hh) {
            gg = (gg + S[n-1][hh]) % MOD;
        }
        return gg;
    }
};
