class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = 10**9 + 7
        n = len(nums)
        m = max(nums)
        f = [[0] * (m+1) for _ in range(n)]
        for j in range(nums[0]+1):
            f[0][j] = 1
        for i in range(1, n):
            for j in range(nums[i]+1):
                for k in range(min(j, nums[i-1] - nums[i] + j) + 1):
                    f[i][j] += f[i-1][k]
            f[i][j] %= M
        return sum(f[-1]) % M
