class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();

        // 初始化二维dp数组
        vector<vector<int>> dp(n, vector<int>(51, 0));

        // 初始化dp[0][x]
        for (int x = 0; x <= nums[0]; ++x) {
            dp[0][x] = 1;  // 第一位置上 x + (nums[0] - x) = nums[0] 时的方案数为1
        }

        // 动态规划填充 dp 数组
        for (int i = 1; i < n; ++i) {
            // 创建前缀和数组
            vector<int> prefixSum(51, 0);
            for (int x = 0; x <= 50; ++x) {
                prefixSum[x] = dp[i-1][x];
                if (x > 0) {
                    prefixSum[x] = (prefixSum[x] + prefixSum[x-1]) % MOD;
                }
            }

            // 计算当前 dp[i][x]
            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                int xx = min(x, nums[i-1] - nums[i] + x);
                if (xx >= 0 && xx <= x) {
                    dp[i][x] = prefixSum[xx];
                }
            }
        }

        // 计算结果
        int result = 0;
        for (int x = 0; x <= nums[n-1]; ++x) {
            result = (result + dp[n-1][x]) % MOD;
        }

        return result;
    }
};
