class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        arr = [0]*(max(nums) + 1)
        M = 10**9+7
        for i in range(nums[0] + 1):
            arr[i] = 1
        for i, num in enumerate(nums[1:]):
            arr_tmp = [0]*(max(nums) + 1)
            lowest = max(0, num - nums[i])
            count = 0
            for j in range(num + 1 - lowest):
                count += arr[j]
                count %= M
                arr_tmp[j + lowest] = count
            arr = arr_tmp
        return sum(arr) % M
