class Solution {
public:
    typedef long long ll;
//     ll compute(ll i,ll j,ll k,vector <int> &nums,vector<vector<vector<ll>>> &dp)
//     {

//     }
    int countOfPairs(vector<int>& nums) 
    {
        ll n=nums.size();
        // ll dp[n+1][51][51];
        vector<vector<vector<ll>>> dp(n+1,vector<vector<ll>>(51,vector<ll>(51)));
        ll mod=1e9+7;
        for(ll i=0;i<=n;i++)
        {
            for(ll j=0;j<=50;j++)
            {
                for(ll k=0;k<=50;k++)
                {
                    dp[i][j][k]=0;
                }
            }
        }
        for(ll i=0;i<n;i++)
        {
            for(ll j=0;j<=50;j++)
            {
                for(ll k=0;k<=50;k++)
                {
                    if(i==0)
                    {
                        if(k<=nums[i])   
                        dp[i][k][nums[i]-k]=1;
                    }
                    else
                    {
                        if(k<=nums[i]&&j<=nums[i-1])
                        {
                            if(k>=j)
                            {
                                if(nums[i]-k<=nums[i-1]-j)
                                {
                                    dp[i][k][nums[i]-k]=(dp[i][k][nums[i]-k]+dp[i-1][j][nums[i-1]-j])%mod;
                                }
                            }
                        }
                    }
                }
            }
        }
        ll ans=0;
        for(ll j=0;j<=50;j++)
        {
            if(nums[n-1]-j>=0)
            {
                ans=(ans+dp[n-1][j][nums[n-1]-j])%mod;
            }
        }
        int ret=ans;
        return ret; 
    }
};
