class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD=10**9+7
        @cache
        def dfs(i, prev_a, prev_b):
            if i == len(nums):
                return 1

            count = 0
            for a in range(prev_a, nums[i] + 1):
                b = nums[i] - a
                if b <= prev_b:
                    count = (count + dfs(i + 1, a, b)) % MOD
            return count

        return dfs(0, 0, 50)
