class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9 + 7;

        int dp[n + 1][52];
        memset(dp, 0, sizeof(dp));

        for(int i = 0; i <= 50; i++)
            dp[n][i] = 1; 

        for(int i = n - 1; i > 0; i--) 
            for(int j = 0; j <= nums[i - 1]; j++) {
                for(int k = j; k <= nums[i]; k++) {
                    if(nums[i] - k <= nums[i - 1] - j)
                        dp[i][j] = (dp[i][j] + dp[i + 1][k]) % mod;
                }
            }

        int ans = 0;
        for(int i = 0; i <= nums[0]; i++)
            ans = (ans + dp[1][i]) % mod;
        return ans;
    }
};

