using ll = long long;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1e9 + 7;
        int n = nums.size();
        vector<ll> dp(51);
        // , dp1(51);
        for (int i = 0; i <= nums[0]; i++) {
            dp[i] = 1;
            // dp1[nums[0] - i] = 1;
        }
        for (int i = 1; i < n; i++) {
            int v = nums[i];
            vector<ll> dp2(51);
            // vector<ll> dp3(51);
            // cout << " --- " << i << endl;
            for (int j = 0; j <= v; j++) {
                // [0, ..., j], j
                // [v - j, ..., 50], v - j

                // 0 2
                // 2 1

                // 1 2
                // 1 1

                // 2 2
                // 0 1

                for (int p = 0; p <= j; p++) {
                    if (nums[i - 1] - p >= nums[i] - j) {
                        dp2[j] = (dp2[j] + dp[p]) % mod;
                    }
                }
                // cout << "j = " << j << " dp2 = " << dp2[j] << endl;
            }

            dp = dp2;
            // dp1 = dp3;
        }
        ll res = 0;
        for (int i = 0; i < 51; i++) {
            res = (res + dp[i]) % mod;
        }
        return res;
    }
};
