class Solution {
public:
    int countOfPairs(vector<int>& a) {
        vector<int> dp(51);
        int n = a.size();
        for (int i = 0; i <= a[0]; i++) {
            dp[i]++;
        }
        int MOD = 1e9 + 7;
        for(int i = 1; i < n; i++) {
            vector<int> nxt(51);
            for (int j = 0; j <= a[i]; j++) {
                for (int k = 0; k <= a[i - 1]; k++) {
                    if (dp[k] > 0) {
                        if (j >= k && a[i] - j <= a[i - 1] - k) {
                            nxt[j] += dp[k];
                            nxt[j] %= MOD;
                        }
                    }
                }
            }
            dp = nxt;
        }
        int res = 0;
        for (int i = 0; i <= 50; i++) {
            res += dp[i];
            res %= MOD;
        }
        return res;
    }
};
