int dp[2005][1005];
const int mod = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = (int)nums.size();
    memset(dp, 0, sizeof(dp));
    for(int i = 0; i <= nums[0]; i++) dp[0][i] = 1;
    for(int i = 1; i < n; i++)
    {
        int sub = max(0, nums[i] - nums[i - 1]);
        for(int j = 0; j <= nums[i]; j++)
        {
            int last = j ? dp[i][j - 1] : 0;
            dp[i][j] = (last + ((j >= sub) ? dp[i - 1][j - sub] : 0)) % mod;
        }
    }
    int ans = 0;
    for(int i = 0; i <= nums[n - 1]; i++)
        ans = (ans + dp[n - 1][i]) % mod;
    return ans;
    }
};
