class Solution {
public:
    int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(),fans=0;
        vector<vector<int>> dp(n,vector<int>(51));
        for(int i=0;i<=nums[0];i++){
            dp[0][i]=1;
        }
        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                for(int prev=0;prev<=nums[i-1];prev++){
                    if((j >= prev)&&(nums[i-1]-prev >= nums[i]-j)){
                        dp[i][j]+=dp[i-1][prev];
                        dp[i][j]=dp[i][j]%mod;
                    }
                }
            }
        }

        for(int j=0;j<dp[n-1].size();j++){
            fans+=dp[n-1][j];
            fans=fans%mod;
        }
        return fans;
    }
};
