class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7
        @lru_cache(None)
        def dp(idx, a, b):
            if idx == len(nums):
                return 1
            res = 0
            for i in range(a, nums[idx]+1):
                if nums[idx] - i > b:
                    continue
                res += dp(idx+1, i,  nums[idx] - i)
                res %= mod
            return res
        return dp(0, 0, float("inf"))
