class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        vector<vector<int>> dp(nums.size(), vector<int>(51));
        for (int i = 0; i <= nums[0]; i++)
            dp[0][i] = 1;
        for (int i = 0; i+1 < nums.size(); i++) {
            for (int j = 0; j < 51; j++) {
                int low = max(j, nums[i+1] - (nums[i] - j));
                int high = nums[i+1];
                for (int k = low; k <= high; k++)
                    dp[i+1][k] = (dp[i+1][k] + dp[i][j]) % MOD;
            }
        }
        int ans = 0;
        for (int cnt : dp.back())
            ans = (ans + cnt) % MOD;
        return ans;
    }
};
