from functools import cache
MOD = 10**9+7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def DP(i, a, b):
            if i == n:
                return 1

            res = 0
            for x in range(a,nums[i]+1):
                y = nums[i] - x
                if y <= b:
                    res = (res + DP(i+1, x, y )) % MOD
            return res

        n = len(nums)
        return DP(0,0,nums[0])
